// ############################################################################
// Chart                                                                  View
// ############################################################################
// Is the base class for all visualization charts
// - Create the div container
// - Provide d3 as the visualization library
// - Provide title, subtitle, data reference
// - Provide functionality for exporting in SVG and PNG
// ############################################################################

class Chart
{
  // ##########################################################################
  // PUBLIC MEMBERS
  // ##########################################################################

  // ==========================================================================
  // Construct chart
  // ==========================================================================

  constructor(main, chartName, climateData)
  {
    // ------------------------------------------------------------------------
    // Member Variables
    // ------------------------------------------------------------------------

    this._main = main
    this._chartName = chartName
    this._climateData = climateData


    // ------------------------------------------------------------------------
    // Setup container
    // ------------------------------------------------------------------------

    let parentDiv = $('#' + main.config.chart.container)

    console.log("created " + this._chartName);



    // ------------------------------------------------------------------------
    // User Interaction
    // ------------------------------------------------------------------------


  }


  // ==========================================================================
  // Update chart
  // ==========================================================================

  update(climateData)
  {
    this._climateData = climateData

    console.log("updated " + this._chartName);
  }


  // ==========================================================================
  // Remove chart
  // ==========================================================================

  remove()
  {
    this._climateData = null

    console.log("removed " + this._chartName);
  }


  // ##########################################################################
  // PRIVATE MEMBERS
  // ##########################################################################



}
